import './App.css';
import { People } from './people.js';
import './index.css';
import { useState } from 'react';

var options = { month: 'long' };
let thisDay = new Date().getDate();
let thisMonth = new Intl.DateTimeFormat('en-US', options).format(new Date());
let thisYear = new Date().getFullYear();

function App() {
	return (
		<div className='bday-box'>
			<h1 className='app-title'>Birthday Reminder App</h1>
			<h3 className='heading3'>Today's Birthdays</h3>
			<hr></hr>
			<Birthdays />
			<button className='btn' onClick={OnClick}>
				Clear All
			</button>
		</div>
	);
}

const CurrentBdays = People.filter(
	(person) => thisDay === person.birthday && thisMonth === person.birthmonth,
);

const Birthdays = () => {
	if (CurrentBdays.length === 0) {
		return <p>No birthdays today</p>;
	} else if (CurrentBdays !== []) {
		return CurrentBdays.map((item) => (
			<section key={item.id}>
				<img className='person-img' src={item.image.default} alt='' />
				<h4 className='person-name'>{item.name}</h4>
				<p className='person-age'>
					{thisYear - item.birthyear} years old
				</p>
			</section>
		));
	}
};

const OnClick = () => {
  const [list, setList] = useState();
  setList();
};

export default App;

