export const People = [
	{
		name: 'John Smith',
		birthmonth: 'January',
		birthday: 5,
		birthyear: 1980,
	},
	{
		name: 'Jennifer Goran',
		birthmonth: 'July',
		birthday: '4',
		birthyear: '1992',
	},
	{
		name: 'Peter Miller',
		birthmonth: 'April',
		birthday: '16',
		birthyear: '1978',
	},
	{
		name: 'Shannon Jenkins',
		birthmonth: 'August',
		birthday: '9',
		birthyear: '1954',
	},
];

var options = { month: 'long' };
let thisDay = new Date().getDate();
let thisMonth = new Intl.DateTimeFormat('en-US', options).format(new Date());

//console.log(new Intl.DateTimeFormat('en-US', options).format(new Date())); //get month
//console.log(new Date().getDate()); // get day

for (let person = 0; person < People.length; person++) {
	console.log(People[person].birthday);
	console.log(thisDay == People[person].birthday);
	if (
		thisDay === People[person].birthday &&
		thisMonth === People[person].birthmonth
	) {
		console.log(person.name);
	}
}

//console.log(People.map((person) => person.birthday));
