//import logo from './logo.svg';
import './App.css';
import { People } from './people.js';
import './index.css';
//import John from './John.jpeg';

var options = { month: 'long' };
let thisDay = new Date().getDate();
let thisMonth = new Intl.DateTimeFormat('en-US', options).format(new Date());
let thisYear = new Date().getFullYear();

function App() {
	return (
		<div className='bday-box'>
			<h1 className='app-title'>Birthday Reminder App</h1>
			<h3 className='heading3'>Today's Birthdays</h3>
			<hr></hr>
			<Birthdays />
			<button className='btn'>Clear All</button>
		</div>
	);
}

const Birthdays = () => {
	for (let person = 0; person < People.length; person++) {
		if (
			thisDay === People[person].birthday &&
			thisMonth === People[person].birthmonth
		) {
			return (
				<>
					<img
						className='person-img'
						src={People[person].image.default}
						alt=''
					/>
					<h4 className='person-name'>{People[person].name}</h4>
					<p className='person-age'>
						{thisYear - People[person].birthyear} years old
					</p>
				</>
			);
		}
	}
};

